INSERT INTO assay_descriptor(NODE_ID, PARENT_NODE_ID, ELEMENT_ID, LABEL, DESCRIPTION, ABBREVIATION, ACRONYM, SYNONYMS, EXTERNAL_URL, UNIT, ELEMENT_STATUS_ID) VALUES (93, 92, 89, 'biochemical format', 'An in vitro format used to measure the activity of a biological macromolecule, either purified protein or nucleic acid; most often a homogenous assay format, but can be heterogeneous if a solid phase (e.g. beads) is used to immobilize the macromolecule.', null, null, null, null, null, 2);
INSERT INTO assay_descriptor(NODE_ID, PARENT_NODE_ID, ELEMENT_ID, LABEL, DESCRIPTION, ABBREVIATION, ACRONYM, SYNONYMS, EXTERNAL_URL, UNIT, ELEMENT_STATUS_ID) VALUES (94, 93, 94, 'nucleic acid format', 'A format in which the perturbagen targets nucleic acid (DNA or RNA) to regulate its function.', null, null, null, null, null, 2);
INSERT INTO assay_descriptor(NODE_ID, PARENT_NODE_ID, ELEMENT_ID, LABEL, DESCRIPTION, ABBREVIATION, ACRONYM, SYNONYMS, EXTERNAL_URL, UNIT, ELEMENT_STATUS_ID) VALUES (95, 93, 95, 'protein format', 'A format in which the perturbagen targets a protein to regulate its function.', null, null, null, null, null, 2);
INSERT INTO assay_descriptor(NODE_ID, PARENT_NODE_ID, ELEMENT_ID, LABEL, DESCRIPTION, ABBREVIATION, ACRONYM, SYNONYMS, EXTERNAL_URL, UNIT, ELEMENT_STATUS_ID) VALUES (96, 92, 90, 'cell-based format', 'A heterogenous assay format that involves living cells of eukaryotic origin.', null, null, null, null, null, 2);
INSERT INTO assay_descriptor(NODE_ID, PARENT_NODE_ID, ELEMENT_ID, LABEL, DESCRIPTION, ABBREVIATION, ACRONYM, SYNONYMS, EXTERNAL_URL, UNIT, ELEMENT_STATUS_ID) VALUES (97, 92, 91, 'cell-free format', 'An in vitro format where biological material originates from cells, but does not use live cells nor purified macromolecules; most often a homogenous assay format, but can be heterogeneous if a solid phase (e.g. beads) is used to immobilize the components.', null, null, null, null, null, 2);
INSERT INTO assay_descriptor(NODE_ID, PARENT_NODE_ID, ELEMENT_ID, LABEL, DESCRIPTION, ABBREVIATION, ACRONYM, SYNONYMS, EXTERNAL_URL, UNIT, ELEMENT_STATUS_ID) VALUES (98, 97, 96, 'sub-cellular format', 'A format using sub-cellular organelles (but not individually purified proteins) obtained by cell lysis and fractionation (e.g., differential centrifugation).', null, null, null, null, null, 2);
INSERT INTO assay_descriptor(NODE_ID, PARENT_NODE_ID, ELEMENT_ID, LABEL, DESCRIPTION, ABBREVIATION, ACRONYM, SYNONYMS, EXTERNAL_URL, UNIT, ELEMENT_STATUS_ID) VALUES (99, 97, 97, 'whole-cell lysate format', 'A format using cells whose membranes have been ruptured (e.g., mechanically, osmotically) and whose lysate is used without separation techniques.', null, null, null, null, null, 2);
