INSERT INTO assay_descriptor(NODE_ID, PARENT_NODE_ID, ELEMENT_ID, LABEL, DESCRIPTION, ABBREVIATION, ACRONYM, SYNONYMS, EXTERNAL_URL, UNIT, ELEMENT_STATUS_ID) VALUES (114, 113, 131, 'cell culture component', 'This describes the specific medium in which a cell line is cultured, which is optimized for its growth. It includes the medium additives namely, serum, growth factors, buffers, amino acids, antibiotics, etc. This information can be obtained from ATCC or found in relevant publications.', null, null, null, null, null, 2);
INSERT INTO assay_descriptor(NODE_ID, PARENT_NODE_ID, ELEMENT_ID, LABEL, DESCRIPTION, ABBREVIATION, ACRONYM, SYNONYMS, EXTERNAL_URL, UNIT, ELEMENT_STATUS_ID) VALUES (115, 114, 326, 'assay medium', 'The cell culture broth used while performing an assay on cells, which is optimized for each assay type. Some interfering additives such as serum, growth factors, buffers, amino acids, antibiotics, etc. might be eliminated in this medium.', null, null, null, null, null, 2);
INSERT INTO assay_descriptor(NODE_ID, PARENT_NODE_ID, ELEMENT_ID, LABEL, DESCRIPTION, ABBREVIATION, ACRONYM, SYNONYMS, EXTERNAL_URL, UNIT, ELEMENT_STATUS_ID) VALUES (116, 114, 327, 'assay serum', 'The serum used in assay medium while performing an assay on cells and is optimized for each assay type. In certain assays, the cells could be maintained at either a lower concentration of the serum used in regular culture or in a specialized serum, including dextran charcoal treated serum, dialyzed serum, etc. This is done to avoid interference with the assay measurements.', null, null, null, null, null, 2);
INSERT INTO assay_descriptor(NODE_ID, PARENT_NODE_ID, ELEMENT_ID, LABEL, DESCRIPTION, ABBREVIATION, ACRONYM, SYNONYMS, EXTERNAL_URL, UNIT, ELEMENT_STATUS_ID) VALUES (117, 114, 328, 'culture serum', 'Cultured cells require serum or growth factors for growth by cell division. Each cell type is grown in a medium supplemented with a variable concentration of serum (up to 20%) which is optimized for its growth. Specialized sera include dextran charcoal treated serum, which lacks certain hormones, growth factors, etc, dialyzed serum, which lacks low molecular weight molecules (below 10,000 MW), such as glucose, amino acids, low molecular weight hormones, cytokines, etc. These sera are used in certain assays to avoid interference from the normal serum components. Most commonly, fetal bovine serum is used in cell culture, but other sera such as horse serum are also used.', null, null, null, null, null, 2);
INSERT INTO assay_descriptor(NODE_ID, PARENT_NODE_ID, ELEMENT_ID, LABEL, DESCRIPTION, ABBREVIATION, ACRONYM, SYNONYMS, EXTERNAL_URL, UNIT, ELEMENT_STATUS_ID) VALUES (118, 114, 329, 'culture medium', 'The liquid broth used to grow cells, which is optimized for each cell type and includes additives such as growth factors, buffers, amino acids, antibiotics, etc. This information can be obtained from ATCC or found in relevant publications.', null, null, null, null, null, 2);
