INSERT INTO assay_descriptor(NODE_ID, PARENT_NODE_ID, ELEMENT_ID, LABEL, DESCRIPTION, ABBREVIATION, ACRONYM, SYNONYMS, EXTERNAL_URL, UNIT, ELEMENT_STATUS_ID) VALUES (137, 132, 321, 'reporter gene (EXTERNAL DICTIONARY', 'A reporter gene is a gene that is attached to a regulatory sequence of another gene of interest and introduced into cultured cells, animals or plants. Certain genes function as reporters because they are easily identified and measured, or because they are selectable markers. Common reporter genes are luciferase, green fluorescent protein (GFP), beta-galactosidase and chloramphenicol acetyltransferase (CAT).', null, null, null, null, null, 2);
INSERT INTO assay_descriptor(NODE_ID, PARENT_NODE_ID, ELEMENT_ID, LABEL, DESCRIPTION, ABBREVIATION, ACRONYM, SYNONYMS, EXTERNAL_URL, UNIT, ELEMENT_STATUS_ID) VALUES (138, 132, 323, 'regulatory region', 'The name of the promoter or artificial regulatory element that was inserted upstream of the reporter gene.', null, null, null, null, null, 2);
INSERT INTO assay_descriptor(NODE_ID, PARENT_NODE_ID, ELEMENT_ID, LABEL, DESCRIPTION, ABBREVIATION, ACRONYM, SYNONYMS, EXTERNAL_URL, UNIT, ELEMENT_STATUS_ID) VALUES (139, 132, 324, 'vector name', 'A vector is an extrachromosomal, self-replicating DNA molecule that is used as a vehicle to transfer the DNA of interest into cells, e.g.,plasmid vector (pGEM-T, pBluescript), lentiviral vector, retroviral vector, etc.', null, null, null, null, null, 2);
