INSERT INTO assay_descriptor(NODE_ID, PARENT_NODE_ID, ELEMENT_ID, LABEL, DESCRIPTION, ABBREVIATION, ACRONYM, SYNONYMS, EXTERNAL_URL, UNIT, ELEMENT_STATUS_ID) VALUES (145, 143, 122, 'signal direction', 'It is the trend of measured readout signal, whether it increases or decreases in perturbagen treated wells, as compared to the untreated or carrier-treated wells in an assay.', null, null, null, null, null, 2);
INSERT INTO assay_descriptor(NODE_ID, PARENT_NODE_ID, ELEMENT_ID, LABEL, DESCRIPTION, ABBREVIATION, ACRONYM, SYNONYMS, EXTERNAL_URL, UNIT, ELEMENT_STATUS_ID) VALUES (146, 143, 124, 'wavelength', 'For fluorescence measurements, it is the wavelength at which the fluorophore is excited and the wavelength at which it emits fluorescence. In the case of absorbance, it is the wavelength at which light is absorbed by a biological entity or a dye.', null, null, null, null, null, 2);
INSERT INTO assay_descriptor(NODE_ID, PARENT_NODE_ID, ELEMENT_ID, LABEL, DESCRIPTION, ABBREVIATION, ACRONYM, SYNONYMS, EXTERNAL_URL, UNIT, ELEMENT_STATUS_ID) VALUES (147, 142, 111, 'assay biosafety level', 'A biosafety level is the level of biocontainment required to isolate hazardous biological agents in an enclosed facility. The levels of containment range from the lowest biosafety level of 1 to the highest at level 4.', null, null, null, null, null, 2);
INSERT INTO assay_descriptor(NODE_ID, PARENT_NODE_ID, ELEMENT_ID, LABEL, DESCRIPTION, ABBREVIATION, ACRONYM, SYNONYMS, EXTERNAL_URL, UNIT, ELEMENT_STATUS_ID) VALUES (148, 142, 112, 'assay condition', 'A set of optimization guidelines used to minimize the time and cost of assay implementation, while providing reliable assay performance.', null, null, null, null, null, 2);
INSERT INTO assay_descriptor(NODE_ID, PARENT_NODE_ID, ELEMENT_ID, LABEL, DESCRIPTION, ABBREVIATION, ACRONYM, SYNONYMS, EXTERNAL_URL, UNIT, ELEMENT_STATUS_ID) VALUES (149, 148, 367, 'incubation temperature', null, null, null, null, null, null, 2);
