INSERT INTO assay_descriptor(NODE_ID, PARENT_NODE_ID, ELEMENT_ID, LABEL, DESCRIPTION, ABBREVIATION, ACRONYM, SYNONYMS, EXTERNAL_URL, UNIT, ELEMENT_STATUS_ID) VALUES (154, 142, 113, 'assay footprint', 'This describes the physical format such as plate density in which an assay is performed, which is generally a microplate format, but can also be an array format.', null, null, null, null, null, 2);
INSERT INTO assay_descriptor(NODE_ID, PARENT_NODE_ID, ELEMENT_ID, LABEL, DESCRIPTION, ABBREVIATION, ACRONYM, SYNONYMS, EXTERNAL_URL, UNIT, ELEMENT_STATUS_ID) VALUES (155, 142, 114, 'assay measurement throughput', 'Assay measurements throughput quality describes the quality of the measurements performed on each sample, such as single concentration, single repetition, concentration-response, multiple repetitions, etc.', null, null, null, null, null, 2);
INSERT INTO assay_descriptor(NODE_ID, PARENT_NODE_ID, ELEMENT_ID, LABEL, DESCRIPTION, ABBREVIATION, ACRONYM, SYNONYMS, EXTERNAL_URL, UNIT, ELEMENT_STATUS_ID) VALUES (156, 142, 115, 'assay quality assessment', 'Commonly used statistical parameters for monitoring assay quality include Z and Z-prime factors. Prior to starting a large screen, and after assay optimization and miniaturization, pilot screens are performed to assess the quality of the assay run and to assess / validate the suitability of a assay for a high-throughput screening run.', null, null, null, null, null, 2);
INSERT INTO assay_descriptor(NODE_ID, PARENT_NODE_ID, ELEMENT_ID, LABEL, DESCRIPTION, ABBREVIATION, ACRONYM, SYNONYMS, EXTERNAL_URL, UNIT, ELEMENT_STATUS_ID) VALUES (157, 142, 116, 'assay readout content', 'This describes the throughput and information content generated. Categorizing multiplexed (i.e. multiple targets measured simultaneously) and multiparametric assays and high content (image-based) and regular (plate reader) assays.', null, null, null, null, null, 2);
INSERT INTO assay_descriptor(NODE_ID, PARENT_NODE_ID, ELEMENT_ID, LABEL, DESCRIPTION, ABBREVIATION, ACRONYM, SYNONYMS, EXTERNAL_URL, UNIT, ELEMENT_STATUS_ID) VALUES (158, 142, 121, 'assay measurement type', 'This describes whether a change in an assay is measured once at one fixed end-point or over a period of time at several time points.', null, null, null, null, null, 2);
INSERT INTO assay_descriptor(NODE_ID, PARENT_NODE_ID, ELEMENT_ID, LABEL, DESCRIPTION, ABBREVIATION, ACRONYM, SYNONYMS, EXTERNAL_URL, UNIT, ELEMENT_STATUS_ID) VALUES (159, 158, 330, 'endpoint assay', 'In this assay, change in activity is measured at one time point.', null, null, null, null, null, 2);
