update element set label ='GI50' , abbreviation = '',  element_status = 'Published', synonyms = '', description = 'The effective concentration of a growth inhibitor, which produces 50% of the maximum possible response for that inhibitor. GI50 is calculated from [(Ti-Tz)/(C-Tz)] x 100 = 50, where Tz is absorbance at time zero, Ti is absorbance in the presence of test drug, and C is absorbance in the control (untreated cells).  As a measure of viable or proliferating cells, different researchers measure different parameters, namely, protein content (by sulforhodamine B staining followed by absorbance measurement), mitochondrial dehydrogenase activity (by 3-(4,5-dimethylthiazol-2-yl)-2,5-diphenyl tetrasodium bromide, known as MTT staining followed by absorbance measurement), expression of proliferation associated antigens (by immunostaining for Ki-67), and ATP content (by using CellTiter-Glo reagent (Promega) followed by luminescence measurement).', ready_for_extraction = 'Ready' , external_url = '', expected_value_type = 'numeric' , add_child_method = 'no', unit_id = 1078, bard_uri = 'http://www.bard.nih.gov/ontology/bard#BARD_0000930', modified_by = 'pclemons' where element_id = 964;
update element set label ='absolute IC50' , abbreviation = '',  element_status = 'Retired', synonyms = '', description = 'Absolute IC50 (calculated from the curve-fit function) is the concentration corresponding to exactly 50 percent inhibition. It is obtained from the curve fit function (back-calculated IC50) and can also be extrapolated. Commonly the relative IC50 is reported; however in cell proliferation assays absolute IC50 is often used. The absolute IC50 can be more reproducible.  Superceded by the statistical modifier "absolute" coupled to the result type.', ready_for_extraction = 'Ready' , external_url = '', expected_value_type = 'none' , add_child_method = 'no', unit_id = 1078, bard_uri = 'http://www.bard.nih.gov/ontology/bard#BARD_0000939', modified_by = 'pclemons' where element_id = 965;
update element set label ='relative IC50' , abbreviation = '',  element_status = 'Retired', synonyms = '', description = 'The concentration of a test substance that produces an inhibitory effect corresponding to 50% of the activity of that particular substance (i.e. 50% of the difference between top and bottom of fitted curve). It can also be described as the concentration corresponding to the inflection point of the curve.   Superceded by the statistical modifier "relative" coupled to the result type.', ready_for_extraction = 'Ready' , external_url = '', expected_value_type = 'none' , add_child_method = 'no', unit_id = 1078, bard_uri = 'http://www.bard.nih.gov/ontology/bard#BARD_0000943', modified_by = 'pclemons' where element_id = 966;
