update element set label ='TGI' , abbreviation = '',  element_status = 'Published', synonyms = '', description = 'TGI is total growth inhibition, which is the effective concentration of a growth inhibitor, which produces 0% relative growth for that inhibitor. Thus TGI signifies a cytostatic effect. tgi is calculated from [(Ti-Tz)/(C-Tz)] x 100 =0, where Ti = Tz and Tz is absorbance at time zero, Ti is absorbance in the presence of test drug, and C is absorbance in the control (untreated cells).  As a measure of viable cells, different researchers measure different parameters, namely, protein content (by sulforhodamine B staining followed by absorbance measurement), mitochondrial dehydrogenase activity (by 3-(4,5-dimethylthiazol-2-yl)-2,5-diphenyl tetrasodium bromide, known as MTT staining followed by absorbance measurement), and ATP content (by using CellTiter-Glo reagent (Promega) followed by luminescence measurement).', ready_for_extraction = 'Ready' , external_url = '', expected_value_type = 'numeric' , add_child_method = 'no', unit_id = 1078, bard_uri = 'http://www.bard.nih.gov/ontology/bard#BARD_0000934', modified_by = 'pclemons' where element_id = 970;
