-- SCRIPT TO INSERT BAO results into the result type table
--
--  schatwin
--  4/18/12
--
-- commit every 10 rows to isolate any failures
--
--

    PARENT_RESULT_TYPE_ID,
    RESULT_TYPE_NAME,
    DESCRIPTION,
    RESULT_TYPE_STATUS_ID,
    BASE_UNIT)
select element_ID,
    decode(element_ID,7,null,parent_element_id),
    label,
    DESCRIPTION,
    element_STATUS_ID,
    null 
from element
connect by parent_element_id = prior element_id
start with element_Id = 7;
commit;
